BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "django_migrations" (
	"id"	integer NOT NULL,
	"app"	varchar(255) NOT NULL,
	"name"	varchar(255) NOT NULL,
	"applied"	datetime NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "django_content_type" (
	"id"	integer NOT NULL,
	"app_label"	varchar(100) NOT NULL,
	"model"	varchar(100) NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "auth_group" (
	"id"	integer NOT NULL,
	"name"	varchar(80) NOT NULL UNIQUE,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "auth_group_permissions" (
	"id"	integer NOT NULL,
	"group_id"	integer NOT NULL,
	"permission_id"	integer NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("group_id") REFERENCES "auth_group"("id"),
	FOREIGN KEY("permission_id") REFERENCES "auth_permission__old"("id")
);
CREATE TABLE IF NOT EXISTS "auth_user_groups" (
	"id"	integer NOT NULL,
	"user_id"	integer NOT NULL,
	"group_id"	integer NOT NULL,
	FOREIGN KEY("group_id") REFERENCES "auth_group"("id"),
	FOREIGN KEY("user_id") REFERENCES "auth_user__old"("id"),
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "auth_user_user_permissions" (
	"id"	integer NOT NULL,
	"user_id"	integer NOT NULL,
	"permission_id"	integer NOT NULL,
	FOREIGN KEY("user_id") REFERENCES "auth_user__old"("id"),
	FOREIGN KEY("permission_id") REFERENCES "auth_permission__old"("id"),
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "auth_permission" (
	"id"	integer NOT NULL,
	"content_type_id"	integer NOT NULL,
	"codename"	varchar(100) NOT NULL,
	"name"	varchar(255) NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("content_type_id") REFERENCES "django_content_type"("id")
);
CREATE TABLE IF NOT EXISTS "auth_user" (
	"id"	integer NOT NULL,
	"password"	varchar(128) NOT NULL,
	"last_login"	datetime,
	"is_superuser"	bool NOT NULL,
	"first_name"	varchar(30) NOT NULL,
	"last_name"	varchar(30) NOT NULL,
	"email"	varchar(254) NOT NULL,
	"is_staff"	bool NOT NULL,
	"is_active"	bool NOT NULL,
	"date_joined"	datetime NOT NULL,
	"username"	varchar(150) NOT NULL UNIQUE,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "mailtasks_subscriberslist" (
	"id"	integer NOT NULL,
	"name"	varchar(150) NOT NULL,
	"user_id"	integer NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("user_id") REFERENCES "auth_user"("id")
);
CREATE TABLE IF NOT EXISTS "mailtasks_template" (
	"id"	integer NOT NULL,
	"name"	varchar(100) NOT NULL,
	"subject"	varchar(200) NOT NULL,
	"body"	text NOT NULL,
	"user_id"	integer NOT NULL,
	FOREIGN KEY("user_id") REFERENCES "auth_user"("id"),
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "mailtasks_variable" (
	"id"	integer NOT NULL,
	"tag_name"	varchar(40) NOT NULL,
	"verbose_name"	varchar(40) NOT NULL,
	"subscribers_list_id"	integer NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("subscribers_list_id") REFERENCES "mailtasks_subscriberslist"("id")
);
CREATE TABLE IF NOT EXISTS "mailtasks_variabledata" (
	"id"	integer NOT NULL,
	"value"	varchar(50) NOT NULL,
	"subscriber_id"	integer NOT NULL,
	"variable_id"	integer NOT NULL,
	FOREIGN KEY("subscriber_id") REFERENCES "mailtasks_subscriber__old"("id"),
	FOREIGN KEY("variable_id") REFERENCES "mailtasks_variable"("id"),
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "mailtasks_task" (
	"id"	integer NOT NULL,
	"name"	varchar(120) NOT NULL,
	"start_time"	datetime,
	"status"	varchar(1) NOT NULL,
	"scheduled"	bool NOT NULL,
	"celery_id"	varchar(36),
	"subscribers_list_id"	integer NOT NULL,
	"template_id"	integer NOT NULL,
	"user_id"	integer NOT NULL,
	FOREIGN KEY("template_id") REFERENCES "mailtasks_template"("id"),
	FOREIGN KEY("user_id") REFERENCES "auth_user"("id"),
	FOREIGN KEY("subscribers_list_id") REFERENCES "mailtasks_subscriberslist"("id"),
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "mailtasks_subscriber" (
	"id"	integer NOT NULL,
	"address"	varchar(100) NOT NULL,
	"subscribers_list_id"	integer NOT NULL,
	FOREIGN KEY("subscribers_list_id") REFERENCES "mailtasks_subscriberslist"("id"),
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "mailtasks_letter" (
	"id"	char(32) NOT NULL,
	"delivery_status"	bool NOT NULL,
	"send_time"	datetime,
	"read_datetime"	datetime,
	"subject"	varchar(200) NOT NULL,
	"content"	text NOT NULL,
	"recipient_id"	integer NOT NULL,
	"task_id"	integer NOT NULL,
	PRIMARY KEY("id"),
	FOREIGN KEY("task_id") REFERENCES "mailtasks_task"("id"),
	FOREIGN KEY("recipient_id") REFERENCES "mailtasks_subscriber"("id")
);
CREATE TABLE IF NOT EXISTS "django_session" (
	"session_key"	varchar(40) NOT NULL,
	"session_data"	text NOT NULL,
	"expire_date"	datetime NOT NULL,
	PRIMARY KEY("session_key")
);
